INSERT INTO `wp_m2ccitours_posts` VALUES("236", "1", "2018-03-16 03:00:54", "2018-03-16 02:00:54", "Bienvenue sur le site dédié à la gestion des rapports de stages du Master 2 CCI !\r\n\r\nIci vous pourrez :\r\n* En tant que visiteur, consulter les diverses informations liées aux stages des étudiants des promotions précédentes ;\r\n* En tant qu\'étudiant de la promotion actuelle du Master 2 CCI, vous pourrez vous connecter via un identifiant pré-défini et déposer votre rapport de stage, votre note de synthèse, ainsi qu\'un résumé de votre stage.\r\n\r\nBonne visite.", "Bienvenue", "", "inherit", "closed", "closed", "", "2-revision-v1", "", "", "2018-03-16 03:00:54", "2018-03-16 02:00:54", "", "2", "http://m2ccitours.cf/2018/03/16/2-revision-v1/", "0", "revision", "", "0");
